mapper.insert:
insert into
MSM_ACC_BOOK
(
a_id
,u_id
,a_date
,a_type
,main_cate
,sub_cate
,payment
,price
,a_memo
)
values
(
SEQ_MSM_ACC_BOOK.nextval
,#{u_id}
,#{a_date}
,#{a_type}
,#{main_cate}
,#{sub_cate}
,#{payment}
,#{price}
,#{a_memo}
)

mapper.select:
SELECT a_id ,u_id ,to_char(a_date,'yy/mm/dd') a_date ,a_type ,main_cate ,sub_cate ,payment ,price ,a_memo FROM MSM_ACC_BOOK
u_id = #{u_id} and a_date between #{start_date} and #{end_date}
and main_cate in
#{item}
and sub_cate in
#{item}
and a_type =#{type}
and payment in
#{item}
and a_memo like '%' || #{keyWord} || '%'
order by a_date asc

		SELECT 
		a_id
		,u_id
		,to_char(a_date,'yy/mm/dd') a_date
		,a_type
		,main_cate
		,sub_cate
		,payment
		,price
		,a_memo FROM MSM_ACC_BOOK
		
			u_id = #{u_id}
			and a_date between #{start_date} and #{end_date}
			
			
				and main_cate in
				
					#{item}
				
			

			
			
				and sub_cate in
				
					#{item}
				
			

			
			
				and a_type =#{type}
			
			
			
				and payment in
				
					#{item}
				
			
			
			
				and a_memo like '%' || #{keyWord} || '%'

mapper.update:
update MSM_ACC_BOOK set
a_date = #{a_date}
,a_type = #{a_type}
,main_cate = #{main_cate}
,sub_cate = #{sub_cate}
,payment = #{payment}
,price = #{price}
,a_memo = #{a_memo}
where
a_id = #{a_id}

