mapper.insert:
insert into
MSM_ACC_BOOK
(
a_id
,u_id
,a_date
,a_type
,main_cate
,sub_cate
,payment
,price
,a_memo
)
values
(
SEQ_MSM_ACC_BOOK.nextval
,#{u_id}
,#{a_date}
,#{a_type}
,#{main_cate}
,#{sub_cate}
,#{payment}
,#{price}
,#{a_memo}
)

mapper.select:
SELECT a_id ,u_id ,to_char(a_date,'yy/mm/dd') a_date ,a_type ,main_cate ,sub_cate ,payment ,price ,a_memo FROM MSM_ACC_BOOK
u_id = #{u_id} and a_date between #{start_date} and #{end_date}
and main_cate in
#{item}
and sub_cate in
#{item}
and a_type =#{type}
and payment in
#{item}
and a_memo like '%' || #{keyWord} || '%'
UNION select a_id , u_id , a_date , a_type , main_cate , sub_cate , payment , price , a_memo from ( select ta_id a_id , u_id , to_char(ta_date,'yy/mm/dd') a_date , ta_type a_type , CASE WHEN ta_type='INC' THEN '수입' WHEN ta_type='OUT' THEN '지출' ELSE 'DEFAULT' END AS main_cate , '경조사비' sub_cate , '현금' payment , ta_price price , substr(ta_memo,7 , length(ta_memo) ) a_memo from msm_target_acc_book mtga inner join msm_target mtg on mtga.t_id = mtg.t_id )
u_id = #{u_id}
and a_type =#{type}
and a_date between #{start_date} and #{end_date} order by a_date asc

SELECT a_id ,u_id ,to_char(a_date,'yy/mm/dd') a_date ,a_type ,main_cate ,sub_cate ,payment ,price ,a_memo FROM MSM_ACC_BOOK
u_id = #{u_id} and a_date between #{start_date} and #{end_date}
and main_cate in
#{item}
and sub_cate in
#{item}
and a_type =#{type}
and payment in
#{item}
and a_memo like '%' || #{keyWord} || '%'
UNION select a_id , u_id , a_date , a_type , main_cate , sub_cate , payment , price , a_memo from ( select ta_id a_id , u_id , to_char(ta_date,'yy/mm/dd') a_date , ta_type a_type , CASE WHEN ta_type='INC' THEN '수입' WHEN ta_type='OUT' THEN '지출' ELSE 'DEFAULT' END AS main_cate , '경조사비' sub_cate , '현금' payment , ta_price price , substr(ta_memo,7 , length(ta_memo) ) a_memo from msm_target_acc_book mtga inner join msm_target mtg on mtga.t_id = mtg.t_id )
u_id = #{u_id}
and a_type =#{type}
and a_date between #{start_date} and #{end_date} order by a_date asc

mapper.update:
update MSM_ACC_BOOK set
a_date = #{a_date}
,a_type = #{a_type}
,main_cate = #{main_cate}
,sub_cate = #{sub_cate}
,payment = #{payment}
,price = #{price}
,a_memo = #{a_memo}
where
a_id = #{a_id}

mapper.select:
		select count(*) from MSM_ACC_BOOK
		
			u_id = #{u_id}
			and a_date between #{start_date} and #{end_date}
			
			
				and main_cate in
				
					#{item}
				
			

			
			
