mapper.insert:
insert into msm_target 
	(
		t_id
		, t_date
		, t_name
		, t_group
		
		, t_birth
		
	)
values
	(
		seq_msm_target.nextval
		, to_date(#{t_date}, 'yyyy-mm-dd')
		, #{t_name}
		, #{t_group}
		
		, #{t_birth}
		
	)

insert into msm_target_acc_book
	(
		ta_id
		, t_id
		, ta_memo
		, ta_type
		, ta_price
		, ta_date
	)
values
	(
		seq_msm_target_acc.nextval
		, #{t_id}
		, #{ta_memo}
		, #{ta_type}
		, #{ta_price}
		, to_date(#{ta_date}, 'yyyy-mm-dd')
	)

mapper.select:
SELECT TO_CHAR(ta_date, 'yyyy-mm-dd') ta_date , ta_memo , t_group , t_name || DECODE(t_birth, null, '', '('||t_birth||')') t_name , ta_price , mt.t_id FROM msm_target_acc_book mt_acc INNER JOIN msm_target mt ON mt_acc.t_id = mt.t_id
t_group LIKE '%' || #{group_name} || '%'
mt.t_id = #{t_id}

SELECT * FROM msm_target
t_name LIKE '%' || #{srch_val} || '%'
t_id = #{t_id}

mapper.update:
update msm_target
set

t_birth = #{t_birth}

where t_id = #{t_id}
