mapper.update:
update MSM_USER set 
					u_id=#{u_id}
				    , u_pwd=#{u_pwd}
				    , u_name=#{u_name}
				    , u_email=#{u_email}
				    , u_phone=#{u_phone}
				    , u_birth=#{u_birth}
				    , u_address=#{u_address}

update MSM_USER set 
	u_emergences=#{u_emergences} 
where 
	u_id=#{u_id}

mapper.insert:
update MSM_SUP_ACC
set
	e_acc=e_acc+#{u_emergences}
where 
	u_id=#{u_id}

insert into MSM_ACC_BOOK(a_id, u_id, a_date, a_type, main_cate, sub_cate, payment, price, a_memo)
values(
	SEQ_MSM_ACC_BOOK.nextval
	, #{u_id}
	, to_date(#{a_date}, 'YYYY-MM-DD')
	, 'in'
	, '변동수입'
	, '변동수입'
	, #{payment}
	, #{price}
	, #{a_memo}
)

insert into MSM_ACC_BOOK(a_id, u_id, a_date, a_type, main_cate, sub_cate, payment, price, a_memo)
values(
	SEQ_MSM_ACC_BOOK.nextval
	, #{u_id}
	, to_date(#{a_date}, 'YYYY-MM-DD')
	, 'out'
	, '변동지출'
	, #{sub_cate}
	, #{payment}
	, #{price}
	, #{a_memo}
)

mapper.update:
